import React, {Component} from 'react';
import { feature } from 'topojson-client';
import axios from 'axios';
import { geoKavrayskiy7 } from 'd3-geo-projection';
import { geoGraticule, geoPath } from 'd3-geo';
import { select as d3Select } from 'd3-selection';

import { WORLD_MAP_URL } from "../constants";

const width = 960;
const height = 600;

class WorldMap extends Component {
    constructor(){
        super();
        this.state = {
            map: null
        }
        // create a ref so that you could get the map
        this.refMap = React.createRef();
    }

    // get data from the backend
    componentDidMount() {
        //.then()  - succeed
        // .catch() - failure
        axios.get(WORLD_MAP_URL)
            .then(res => {
                const { data } = res;
                const land = feature(data, data.objects.countries).features;
                this.generateMap(land);
            })
            .catch(e => console.log('err in fecth world map data ', e))
    }

    generateMap(land){
        // create an projection map and set the parameters
        const projection = geoKavrayskiy7()
            .scale(170)
            .translate([width / 2, height / 2])
            .precision(.1);

        // this library provides latitude and longitude data
        const graticule = geoGraticule();

        // get the 画图位置
        const canvas = d3Select(this.refMap.current)
            .attr("width", width)
            .attr("height", height);

        // 画布
        let context = canvas.node().getContext("2d");

        // 真实数据和path进行投影
        let path = geoPath()
            .projection(projection)
            .context(context);

        // ele - each data
        land.forEach(ele => {
            // draw one path
            context.fillStyle = '#B3DDEF';
            context.strokeStyle = '#000';
            context.globalAlpha = 0.75;
            context.beginPath();
            path(ele);
            context.fill();
            context.stroke();

            // draw the longitude and altitude
            context.strokeStyle = 'rgba(220, 220, 220, 0.1)';
            context.beginPath();
            path(graticule());
            context.lineWidth = 0.1;
            context.stroke();

            context.beginPath();
            context.lineWidth = 0.5;
            path(graticule.outline());
            context.stroke();
        })
    }

    render() {
        return (
            <div className="map-box">
                <canvas className="map" ref={this.refMap} />
            </div>
        );
    }
}

export default WorldMap;
